ninjApp.controller("menuController",function(e,t){e.hoverIn=function(e){$(".menuOverlay-mainMenu, .menuOverlay-mainMenu li").addClass("menuOverlay-mainMenu--notSelected");$(".menuOverlay-mainMenu, .menuOverlay-mainMenu li:nth-child("+e+")").removeClass("menuOverlay-mainMenu--notSelected").addClass("menuOverlay-mainMenu--selected")};e.hoverOut=function(e){$(".menuOverlay-mainMenu, .menuOverlay-mainMenu li").removeClass("menuOverlay-mainMenu--notSelected").removeClass("menuOverlay-mainMenu--selected")}});